import React, { Component } from 'react';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';

const styles = {
    card: {
        maxWidth: 345,
    },
    media: {
        height: 200,
        objectFit: 'cover',
        margin: 10
    },
    container: {
        // margin: 20,
        flexGrow: 1
    }
};



export default class MenuExamplePointing extends Component {
    constructor(props) {
        super(props)
        this.state = { play:true }
        this.lion = "https://s3-ap-southeast-1.amazonaws.com/photo.all/Lion+Roar-SoundBible.com-718441804.mp3"
        this.audioLion = new Audio(this.lion)
    }

    lionClick() {
        this.audioLion.play()
    }

    render() {
        return (
            <div style={styles.container}>
                <Grid container spacing={1}>
                    <Grid item xs={6} sm={3}>
                        <Card style={styles.card}>
                            <CardActionArea onClick={this.lionClick.bind(this)}>
                                    <CardMedia
                                        style={styles.media}
                                        image="https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg"
                                        title="Lion King"
                                    />
                                    <CardContent>
                                        <Typography gutterBottom variant="h5" component="h2">
                                            LION
                                    </Typography>
                                    </CardContent>
                            </CardActionArea>
                        </Card>
                    </Grid>

                    <Grid item xs={6} sm={3}>
                        <Card style={styles.card}>
                            <CardActionArea onClick={this.lionClick.bind(this)}>
                                    <CardMedia
                                        style={styles.media}
                                        image="https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg"
                                        title="Lion King"
                                    />
                                    <CardContent>
                                        <Typography gutterBottom variant="h5" component="h2">
                                            LION
                                    </Typography>
                                    </CardContent>
                            </CardActionArea>
                        </Card>
                    </Grid>

                </Grid>
            </div>
            )
        }
    }
