import React, { Component } from 'react';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';

const styles = {
    card: {
        maxWidth: 345,
    },
    media: {
        height: 200,
        objectFit: 'cover',
        margin: 10
    },
    container: {
        margin: 20
    }
};



export default class MenuExamplePointing extends Component {
    constructor(props) {
        super(props)
        this.state = {
            play:true
        }
        this.lizard = "https://s3-ap-southeast-1.amazonaws.com/photo.all/Lion+Roar-SoundBible.com-718441804.mp3",
        this.audioLizard = new Audio(this.lizard)
    }

    lizardClick() {
        this.audioLizard.lizardClick()
    }

    render() {
        return (
            <div style={styles.container}>
                <Card style={styles.card}>
                    <CardActionArea>
                            <CardMedia
                                style={styles.media}
                                image="https://s3-us-west-1.amazonaws.com/contentlab.studiod/getty/b00a97ddb78e42df84a9c84793e26c48"
                                title="Contemplative Reptile"
                                onClick={this.lizardClick.bind(this)}
                            />
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                                    Lizard
                            </Typography>
                            </CardContent>
                    </CardActionArea>
                </Card>
            </div>
            )
        }
    }
