import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';

const styles = {
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundImage: `url(${'https://animal-sounds.net/instruments/animals/images/backgrounds/forest_animals.jpg'})`,
    },
    gridList: {
        width: 1000,
        height: 940,
    },
};

const tileData = [
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 2,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 1,
    },
    {
        img: 'https://static.euronews.com/articles/stories/03/39/11/44/880x495_cmsv2_72eabe35-c105-57e5-94cf-a0380c44f60a-3391144.jpg',
        title: 'LION',
        cols: 2,
    },

];


export default class MenuExamplePointing extends Component {
    constructor(props) {
        super(props)
        this.state = { play: true }
        this.lion = "https://s3-ap-southeast-1.amazonaws.com/photo.all/Lion+Roar-SoundBible.com-718441804.mp3"
        this.audioLion = new Audio(this.lion)
    }

    lionClick() {
        this.audioLion.play()
    }

    render() {
        return (

            <div style={styles.root}>
                <GridList cellHeight={250} className={styles.gridList} cols={3}>
                    {tileData.map(tile => (
                        <GridListTile key={tile.img} cols={tile.cols || 1}
                        >
                            <img onClick={this.lionClick.bind(this)} src={tile.img} alt={tile.title} />
                        </GridListTile>
                    ))}
                </GridList>
            </div>

        )
    }
}


